function IntroduireMessage( 
	m, 
	etat 
) { 
	var p = document.createElement(
		"p" 
	); 
	p.appendChild( 
		document.createTextNode( 
			m 
		)
	); 
	p.setAttribute( 
		"etat", 
		etat 
	); 
	document.querySelector( 
		"#liste" 
	).appendChild( 
		p 
	); 
} 

function IntroduireSecteur_MaJ(
	secteurTitre, 
	fluxAjoutes 
) { 
	var objStockage = window.db.transaction( 
		"secteurs", 
		"readwrite" 
	).objectStore( 
		"secteurs" 
	); 
	var r = objStockage.add({ 
		"titre" : secteurTitre, 
		"flux" : fluxAjoutes 
	}); 
	r.onsuccess = (evtBDD) => { 
		IntroduireMessage( 
			`Fin de l'ajout du secteur "${secteurTitre}"`, 
			"correct" 
		); 
		Chargement(false); 
	}; 
	r.onerror = (evtBDD) => { 
		evtBDD.preventDefault(); 
		secteurTitre = secteurTitre; 
		fluxAjoutes = fluxAjoutes; 
		objStockage.openCursor().onsuccess = (ssEvtBDD) => { 
			var curseur = ssEvtBDD.target.result; 
			if (curseur.value.titre==secteurTitre) { 
				curseur.value.flux = curseur.value.flux.concat( 
					fluxAjoutes 
				); 
			} 
			var ssR = objStockage.put( 
				curseur.value 
			)
			ssR.onsuccess = () => { 
				IntroduireMessage( 
					`Secteur  "${secteurTitre}" mis à jour`, 
					"correct" 
				); 
				Chargement(false); 
			}; 
			ssR.onerror = () => { 
				IntroduireMessage( 
					`Impossible de mettre à jour le secteur "${secteurTitre}"`, 
					"incorrect" 
				); 
				Chargement(false); 
			}; 
		} 
	} 
} 

function IntroduireSecteur( 
	evt 
) { 
	evt.preventDefault(); 
	Chargement(true); 
	HTML_nettoyer( 
		document.querySelector( 
			"#liste" 
		) 
	); 
	var objStockage = window.db.transaction( 
		"flux", 
		"readwrite" 
	).objectStore( 
		"flux" 
	); 
	var fluxAjoutes = []; 
	var secteurId = evt.target.getAttribute("secteurId"); 
	var flux = window.jonctionsIntro[secteurId]; 
	objStockage.transaction.secteurId = secteurId; 
	objStockage.transaction.oncomplete = (evtBDD) => { 
		var secteurId = objStockage.transaction.secteurId; 
		IntroduireSecteur_MaJ( 
			window.secteursIntro[secteurId], 
			fluxAjoutes, 
		);  
	}; 
	for(var i=0; i<flux.length; i++) { 
		var _flux = window.entreesIntro[flux[i]]; 
		var r = objStockage.add({ 
			"site": _flux.titre, 
			"url": _flux.url, 
			"actif": (_flux.actif==1)?true:false, 
			"etat": _flux.etat 
		}); 
		r.fluxTitre = _flux.titre; 
		r.onsuccess = (evtBDD) => { 
			fluxAjoutes.push( 
				evtBDD.target.result 
			); 
			IntroduireMessage( 
				"Ajout de : "+evtBDD.target.fluxTitre, 
				"correct" 
			); 
		}; 
		r.onerror = (evtBDD) => { 
			evtBDD.preventDefault(); 
			IntroduireMessage( 
				"Ajout de : "+evtBDD.target.fluxTitre, 
				"incorrect" 
			); 
		}; 
	} 
} 
