function Introduire(
	el_formulaire 
) { 
	Chargement(true); 
	var _voirDetailsSecteur = (evt) => { 
		evt.preventDefault(); 
		var secteurId = evt.target.getAttribute("secteurId"); 
		var el = el_formulaire.querySelector("#secteur"+secteurId+""); 
		el.style.display = (el.style.display=="none")?"block":"none"; 
	}; 
	for(var sId in window.secteursIntro) { 
		window.jonctionsIntro[sId] = []; 
	} 
	for(var i=0; i<window.entreesIntro.length; i++) { 
		var flux = window.entreesIntro[i]; 
		for(var sId in window.secteursIntro) { 
			if (flux[sId]!="") 
				window.jonctionsIntro[sId].push(i); 
		} 
	} 

	var elIntro = Gabarit( 
		"introduire", 
		{}, 
		(el) => { 
			return el; 
		} 
	); 
	for(var sId in window.secteursIntro) { 
		if (sId!="1") {
			var elP = Gabarit( 
				"introduireSecteur", 
				{
					"secteurId" : sId, 
					"secteurTitre" : window.secteursIntro[sId] 
				}, 
				(el) => { 
					el.querySelector("a").addEventListener( 
						"click", 
						_voirDetailsSecteur
					); 
					el.querySelector("input").addEventListener( 
						"click", 
						IntroduireSecteur
					); 
					el.querySelector("#secteur"+sId).style.display = "none"; 
					return el;  
				} 
			); 
			for(var i=0; i<window.jonctionsIntro[sId].length; i++) { 
				var fluxId = window.jonctionsIntro[sId][i]; 
				elP.querySelector("div.secteurFlux").appendChild( 
					Gabarit( 
						"introduireSecteurFlux", 
						{ 
							"fluxId" : fluxId, 
							"fluxURL" : window.entreesIntro[fluxId].url, 
							"fluxTitre" : window.entreesIntro[fluxId].titre 
						}, 
						(el) => { 
							el.querySelector("input").addEventListener( 
								"click", 
								IntroduireFlux 
							); 
							return el; 
						} 
					) 
				); 
			} 
			elIntro.appendChild( 
				elP 
			); 
		}
	} 
	el_formulaire.appendChild( 
		elIntro 
	); 
	HTML_titraille(); 
	Chargement(false); 
} 

function Nettoyage_lancer(jours) { 
	Chargement(true); 
	var flux = {}; 
	var dateMax = new Date(); 
	dateMax.setDate( 
		dateMax.getDate()-parseInt(jours) 
	); 
	var objStockage = window.db.transaction( 
		"articles", 
		"readwrite" 
	).objectStore( 
		"articles" 
	); 
	objStockage.transaction.oncomplete = (evtBDD) => { 
		Chargement(false); 
	}; 
	objStockage.openCursor().onsuccess = (evtBDD) => { 
		var curseur = evtBDD.target.result; 
		if (curseur) { 
			var article = curseur.value; 
			var i = 1; 
			if (
				new Date( 
					article.articleDate 
				).getTime()<dateMax.getTime() 
			) 
				i *= 2; 
			if ( 
				!article.articleSuivi 
				&& 
				article.articleVues>0 
			) 
				i *= 3; 
			if (i==6) { 
				if (!(article["flux.titre"] in flux)) 
					flux[article["flux.titre"]] = []; 
				flux[article["flux.titre"]].push( 
					article.id 
				); 
			} 
			curseur.continue(); 
		} else { 
			var elP = document.querySelector("#liste"); 
			var nbre = 0; 
			var liste = document.createElement( 
				"ul" 
			); 
			for(var fluxTitre in flux) { 
				var _flux = flux[fluxTitre]; 
				var li = document.createElement("li"); 
				li.appendChild( 
					document.createTextNode( 
						fluxTitre+" ("+_flux.length+")" 
					) 
				); 
				_flux.forEach( 
					(articleId) => { 
						objStockage.delete( 
							articleId 
						); 
					} 
				); 
				liste.appendChild( 
					li 
				); 
				nbre++; 
			} 
			HTML_nettoyer( 
				elP 
			); 
			if (nbre==0) { 
				elP.appendChild( 
					document.createTextNode( 
						"Aucun article n'a correspondu aux critères." 
					)
				); 
			} else { 
				elP.appendChild( 
					liste 
				); 
			} 
		}
	}; 

}

function Nettoyer(
	el_formulaire 
) { 
	Gabarit( 
		"nettoyer", 
		{}, 
		(element) => { 
			element.querySelector("form").addEventListener( 
				"submit", 
				(evt) => { 
					evt.preventDefault(); 
					if (confirm("Êtes-vous sûr ? Les articles supprimés ne pourront pas être récupéré.")) 
						Nettoyage_lancer( 
							evt.target.querySelector("input[name=nbreJours]").value 
						); 
				} 
			); 
			el_formulaire.appendChild(
				element 
			); 
			HTML_titraille(); 
		}
	); 
}

function Supprimer(
	evt 
) { 
	evt.preventDefault(); 
	var type = evt.target.getAttribute( 
		"_type" 
	); 
	var f = document.querySelector( 
		"#formulaire form" 
	); 
	f.setAttribute( 
		"encours", 
		"oui" 
	); 
	var _fct = (m, etat) => { 
		f.setAttribute( 
			"encours", 
			"non" 
		); 
		var elM = f.querySelector( 
			"span.resultat" 
		); 
		HTML_nettoyer( 
			elM 
		); 
		elM.appendChild(
			document.createTextNode(
				m 
			) 
		); 
		if (etat!=undefined) 
			elM.setAttribute( 
				"etat", 
				etat 
			); 

	}; 
	var r = window.db.transaction( 
		(type=="flux")?"flux":"secteurs", 
		"readwrite" 
	).objectStore( 
		(type=="flux")?"flux":"secteurs" 
	).delete( 
		parseInt( 
			evt.target.getAttribute( 
				(type=="flux")?"fluxId":"secteurId" 
			) 
		) 
	); 
	r.onsuccess = (evtBDD) => { 
		_fct( 
			(type=="flux")? 
				"Le flux a été supprimé de la base." 
			: 
				"Le secteur a été correctement supprimé de la base.", 
			"correct" 
		); 
		var b = f.querySelector( 
			"input[type=submit]" 
		); 
		b.parentElement.parentElement.removeChild( 
			b.parentElement 
		); 
		var liste = document.querySelector( 
			"#liste" 
		); 
		HTML_nettoyer( 
			liste
		); 
		Peupler( 
			type, 
			liste 
		); 
	}; 
	r.onerror = (evtBDD) => { 
		_fct( 
			"Une erreur est survenue lors de la suppression (existe t-il ?).", 
			"incorrect" 
		); 
	}; 
} 

function Sauvegarder(
	evt 
) { 
	evt.preventDefault(); 
	var f = document.querySelector( 
		"#formulaire form" 
	); 
	f.setAttribute( 
		"encours", 
		"oui" 
	); 
	var type = f.getAttribute( 
		"type" 
	); 
	var _fct = (m, etat) => { 
		f.setAttribute( 
			"encours", 
			"non" 
		); 
		var elM = evt.target.querySelector( 
			"span.resultat" 
		); 
		HTML_nettoyer( 
			elM 
		); 
		elM.appendChild(
			document.createTextNode(
				m 
			) 
		); 
		if (etat!=undefined) 
			elM.setAttribute( 
				"etat", 
				etat 
			); 

	}; 
	var objet = {}; 
	evt.target.querySelectorAll( 
		"input[type=text], input[type=hidden]" 
	).forEach( 
		(element) => { 
			switch(element.getAttribute("name")) { 
				case "secteurId": 
				case "fluxId": 
					objet["id"] = element.value; 
					break; 
				case "secteurTitre": 
				case "fluxTitre": 
					objet["titre"] = element.value; 
					break; 
				case "fluxURL": 
					objet["url"] = element.value; 
					break; 
			}
		} 
	); 
	if (type=="flux")
		if (objet.titre=="" || objet.url=="") 
			return _fct(
				"Aucun champ de votre formulaire ne peut rester vide.", 
				"incorrect" 
			); 
	if (type=="secteur")
		if (objet.titre=="") 
			return _fct(
				"Aucun champ de votre formulaire ne peut rester vide.", 
				"incorrect" 
			); 
	objet = (type=="flux")? 
		{ 
			"actif": true, 
			"etat": "HTTP-200", 
			"id": objet.id, 
			"site": objet.titre, 
			"url": objet.url 
		} 
	: 
		{ 
			"id": objet.id, 
			"titre": objet.titre, 
			"flux": [] 
		}; 
	var objStockage = window.db.transaction( 
		(type=="flux")?"flux":"secteurs", 
		"readwrite" 
	).objectStore( 
		(type=="flux")?"flux":"secteurs" 
	); 
	var succes = (evtBDD) => { 
		f.setAttribute( 
			"encours", 
			"non" 
		); 
		_fct(
			(type=="flux")? 
				"L'URL a été inscrite comme un nouveau flux à récupérer." 
			: 
				"Le secteur a bien été enregistré.", 
			"correct" 
		); 
		Peupler( 
			type, 
			document.querySelector("#liste") 
		); 
	}; 
	var echec = (evtBDD) => { 
		f.setAttribute( 
			"encours", 
			"non" 
		); 
		_fct(
			(type=="flux")? 
				"Une erreur est survenue : les URLs ne peuvent pas avoir de doublons." 
			: 
				"Une erreur est survenue : les titres de secteurs ne peuvent pas avoir de doublons." 
			, 
			"incorrect" 
		); 
	}; 
	if (objet.id=="nouveau") { 
		delete objet.id; 
		if (type=="flux") 
			objet.flux = []; 
		var r = objStockage.add( 
			objet 
		); 
		r.onsuccess = (evtBDD) => { 
			_fct( 
				"Le secteur a bien été ajouté.", 
				"correct" 
			)
		}; 
		r.onerror = (evtBDD) => { 
			_fct( 
				"Le secteur n'a pas pu être ajouté (doublon de titre ?).", 
				"incorrect" 
			)
		}; 
	} else { 
		objet.id = parseInt(objet.id); 
		if (type=="flux") { 
			var r = objStockage.put( 
				objet 
			); 
			r.onsuccess = succes; 
			r.onerror = echec; 
		} else { 
			objStockage.get( 
				objet.id 
			).onsuccess = (evtBDD) => { 
				f.setAttribute( 
					"encours", 
					"non" 
				); 
				objet.flux = evtBDD.target.result.flux; 
				var r = objStockage.put( 
					objet 
				); 
				r.onsuccess = succes; 
				r.onerror = echec; 
			}; 
		} 
	} 
}

function Ajouter( 
	type, 
	elP 
) { 
	Chargement(true); 
	Gabarit( 
		(type=="flux")?"editionFlux_formulaire":"editionSecteur_formulaire", 
		(
			 (type=="flux")? 
			 	{ 
					"titre":"Editer un nouveau flux", 
					"fluxId": "nouveau", 
					"fluxTitre": "sans nom", 
					"fluxURL": ""
				} 
			: 
			 	{ 
					"titre":"Editer un nouveau secteur", 
					"secteurId": "nouveau", 
					"secteurTitre": "sans nom" 
				} 
		), 
		(element) => { 
			var b = element.querySelector("input[type=button]"); 
			b.parentElement.removeChild( 
				b 
			); 
			elP.appendChild( 
				element 
			); 
			element.addEventListener( 
				"submit", 
				Sauvegarder 
			); 
			Chargement(false); 
		}
	); 
} 

function Editer(
	evt 
) { 
	Chargement(true); 
	window.location.hash = "formulaire"; 
	var elP = document.querySelector("#formulaire"); 
	var type = evt.target.getAttribute( 
		"type" 
	); 
	HTML_nettoyer( 
		elP
	); 
	evt.target.setAttribute( 
		"etat", 
		"attente" 
	); 

	var objId = evt.target.getAttribute( 
		(type=="flux")?"fluxId":"secteurId" 
	); 
	window.db.transaction( 
		(type=="flux")?"flux":"secteurs", 
		"readonly" 
	).objectStore( 
		(type=="flux")?"flux":"secteurs" 
	).get(
		parseInt(objId) 
	).onsuccess = (evtBDD) => {  
		var objet = evtBDD.target.result; 
		Gabarit( 
			(type=="flux")?"editionFlux_formulaire":"editionSecteur_formulaire", 
			(type=="flux")? 
				{ 
					"titre": "Editer &laquo;&nbsp;{{fluxTitre}}&nbsp;&raquo; ", 
					"fluxId": objet.id, 
					"fluxTitre": objet.site, 
					"fluxURL": objet.url 
				} 
			: 
				{ 
					"titre": "Editer &laquo;&nbsp;{{secteurTitre}}&nbsp;&raquo; ", 
					"secteurId": objet.id, 
					"secteurTitre": objet.titre 
				}, 
			(element) => { 
				element.querySelector("form").addEventListener( 
					"submit", 
					Sauvegarder 
				); 
				var b = element.querySelector("input[type=button]"); 
				b.addEventListener( 
					"click", 
					Supprimer 
				); 
				evt.target.setAttribute( 
					"etat", 
					"" 
				); 
				elP.appendChild( 
					element 
				); 
				if (type!="flux") 
					return Chargement(false); 
				Jonction( 
					objet.id, 
					() => { 
						Chargement(false); 
					}
				); 
			} 
		); 
	}
	
} 

function Peupler( 
	type, 
	el_liste 
) { 
	Chargement(true); 
	HTML_nettoyer( 
		el_liste 
	); 
	var fieldset = Gabarit( 
		(type=="flux")?"editionFlux":"editionSecteurs", 
		{}, 
		(element) => { 
			return el_liste.appendChild( 
				element 
			); 
		}
	); 
	window.db.transaction( 
		(type=="flux")?"flux":"secteurs", 
		"readonly" 
	).objectStore( 
		(type=="flux")?"flux":"secteurs" 
	).openCursor().onsuccess = (evtBDD) => { 
		var curseur = evtBDD.target.result; 
		if (curseur) { 
			Gabarit( 
				(type=="flux")?"editionFlux_item":"editionSecteurs_item", 
				(
					(type=="flux")? 
						{ 
							"fluxId": curseur.value.id, 
							"fluxTitre": curseur.value.site, 
							"fluxURL": curseur.value.url 
						}
					:
						{
							"secteurId": curseur.value.id, 
							"secteurTitre": curseur.value.titre
						} 
				), 
				(element) => { 
					element = element.querySelector("a"); 
					element.addEventListener( 
						"click", 
						Editer 
					); 
					fieldset.appendChild( 
						element 
					); 
				} 
			); 
			curseur.continue(); 
		} else { 
			Chargement(false); 
		} 
	}; 
} 

function Jonction_modifier( 
	evt 
) { 
	evt.preventDefault(); 
	var conteneur = document.querySelector("#conteneur"); 
	conteneur.setAttribute( 
		"etat", 
		"attente" 
	); 
	var fluxId = parseInt(evt.target.getAttribute( 
		"fluxId" 
	)); 
	var jonctionEtat = (evt.target.getAttribute( 
		"jonction" 
	)=="oui")?true:false; 
	var objStockage = window.db.transaction( 
		"secteurs", 
		"readwrite" 
	).objectStore( 
		"secteurs" 
	); 
	objStockage.get( 
		parseInt(evt.target.getAttribute( 
			"secteurId" 
		))
	).onsuccess = (evtBDD) => { 
		var objSecteur = evtBDD.target.result; 
		if (jonctionEtat) { 
			var cle = objSecteur.flux.indexOf(
				fluxId 
			); 
			delete objSecteur.flux[cle]; 
		} else { 
			objSecteur.flux.push( 
				fluxId 
			); 
		} 
		objStockage.put( 
			objSecteur 
		).onsuccess = (ssEvtBDD) => { 
			evt.target.setAttribute( 
				"jonction", 
				(evt.target.getAttribute( 
					"jonction" 
				)=="oui")?"non":"oui"
			); 
			conteneur.setAttribute( 
				"etat", 
				"" 
			); 
		}; 
	}; 
} 

function Jonction(
	fluxId, 
	_SuiteOk 
) { 
	var elListe = document.querySelector("#liste"); 
	HTML_nettoyer( 
		elListe 
	); 
	var elP = Gabarit( 
		"editionJonctions", 
		{}, 
		(element) => { 
			return element.querySelector("fieldset"); 
		} 
	); 
	elP = elListe.appendChild(
		elP 
	); 
	window.db.transaction( 
		"secteurs", 
		"readonly" 
	).objectStore( 
		"secteurs" 
	).openCursor().onsuccess = (evtBDD) => { 
		var curseur = evtBDD.target.result; 
		if (curseur) { 
			if (typeof curseur.value["flux"]=="undefined") 
				curseur.value.flux = []; 
			Gabarit( 
				"editionJonctions_item", 
				{
					"jonction": (curseur.value.flux.indexOf(
						fluxId 
					)>-1)?"oui":"non", 
					"fluxId": fluxId, 
					"secteurId": curseur.value.id, 
					"secteurTitre": curseur.value.titre 
				}, 
				(element) => { 
					element.addEventListener( 
						"click", 
						Jonction_modifier 
					); 
					elP.appendChild( 
						element 
					); 
				}
			); 
			curseur.continue(); 
		} else { 
			_SuiteOk(); 
			//
		}
	}; 
}

function __chargement__() { 


	document.querySelectorAll("#choix a").forEach( 
		(el) => { 
			el.addEventListener( 
				"click", 
				(evt) => { 
					evt.preventDefault(); 
					window.location.hash = "liste"; 
					var el_liste = document.querySelector("#liste"); 
					HTML_nettoyer( 
						el_liste
					); 
					var el_formulaire = document.querySelector("#formulaire"); 
					HTML_nettoyer( 
						el_formulaire
					); 
					switch( 
						evt.target.getAttribute( 
							"type" 
						) 
					) { 
						case "introduire": 
							Introduire( 
								el_formulaire 
							); 
							break; 
						case "nettoyer": 
							Nettoyer( 
								el_formulaire 
							); 
							break; 
						case "fluxAjouter": 
							Ajouter( 
								"flux", 
								el_formulaire 
							); 
							break; 
						case "fluxModifier": 
							Peupler( 
								"flux", 
								el_liste 
							); 
							break; 
						case "secteurAjouter": 
							Ajouter( 
								"secteurs", 
								el_formulaire 
							); 
							break; 
						case "secteursModifier": 
							Peupler( 
								"secteurs", 
								el_liste 
							); 
							break; 
						case "recuperateur": 
							Page_auto("recuperateur"); 
							break; 
						case "outils": 
							Page_auto("outils"); 
							break; 
					}
				} 
			); 
		} 
	); 
	HTML_titraille(); 
	Chargement(false); 

}


__auto__(); 

